lines = word.split('\n')


# check if the foFind char is around the center and return the list of indexes if found
def hasChar(lines, i, j, diffX, diffY, toFind):
    if (0 <= i + diffY < len(lines)) and (0 <= j + diffX < len(lines[i])):
        return (i + diffY, j + diffX) if lines[i + diffY][j + diffX] == toFind else None


def findMs(lines, i, j):
    res = []
    for y in range(max(i - 1, 0), min(i + 2, len(lines))):
        for x in range(max(j - 1, 0), min(j + 2, len(lines[i]))):
            if lines[y][x] == 'M':
                diffX = x - j
                diffY = y - i
                res.append((i + diffY, j + diffX, diffX, diffY))
    return res


# part one
res = 0
for i in range(len(lines)):
    for j in range(len(lines[i])):
        if lines[i][j] == 'X':
            queue = findMs(lines,i, j)
            for c in 'AS':
                if not queue:
                    break
                follow = []
                for y, x, diffX, diffY in queue:
                    tmp = hasChar(lines,y,x,diffX,diffY,c)
                    if tmp:
                        newY,newX = hasChar(lines,y,x,diffX,diffY,c)
                        follow.append((newY,newX,diffX,diffY))
                queue = follow
            res += len(queue)
print(f'Number of XMAS is {res}')

# part two

def isCross(lines,i,j):
    upLeft = lines[i-1][j-1]
    upRight = lines[i-1][j+1]
    downLeft = lines[i+1][j-1]
    downRight = lines[i+1][j+1]
    upLeftToDownRight = (upLeft == 'M' and downRight == 'S') or (upLeft == 'S' and downRight == 'M')
    downLeftToUpRight = (downLeft == 'M' and upRight == 'S') or (downLeft == 'S' and upRight == 'M')
    return upLeftToDownRight and downLeftToUpRight

res = 0

for i in range(1,len(lines) -1 ):
    for j in range(1,len(lines[i]) -1 ):
        if lines[i][j] == 'A':
            res += 1 if isCross(lines,i,j) else 0

print(f'Number of X-MAS is {res}')

